# Function to generate the plot based on the selected dataset
def generate_plot(df, x_selection, y_selection=None):
    if y_selection is None:
        # If only one variable is selected, show its count distribution
        fig = px.histogram(df, x=x_selection)
    else:
        # If two variables are selected, show a relationship graph (scatter or bar as appropriate)
        if df[x_selection].dtype in ['float64', 'int64'] and df[y_selection].dtype in ['float64', 'int64']:
            fig = px.scatter(df, x=x_selection, y=y_selection, color=y_selection)
        else:
            fig = px.bar(df, x=x_selection, y=y_selection, color=y_selection)
    return fig

