# Function to map inputs to their numeric values
def map_inputs(age, course, gender, cgpa, stress_level, depression_level, anxiety_level, sleep_quality, physical_activity, financial_stress, semester_credits, therapy, extracurricular):
    # Initialize the input vector with all zeros for one-hot encoded parts
    input_vector = [0] * 16  # Adjust the size based on the number of features after one-hot encoding
    
    # Directly map numerical and ordinal inputs
    input_vector[0] = age  # Age
    input_vector[1] = cgpa  # CGPA
    input_vector[2] = financial_stress  # Financial Stress
    input_vector[3] = semester_credits  # Semester Credit Load
    
        # Calculate the mental status and map it
    mental_status_score = stress_level + depression_level + anxiety_level
    # Assuming thresholds for the combined score to determine the mental status
    mental_status_thresholds = {
        (0, 4): 1,   # Good
        (5, 8): 0,  # Average
        (9, 15): -1 # Poor
    }
    
    # Determine mental status based on the score using the thresholds
    for (lower_bound, upper_bound), status in mental_status_thresholds.items():
        if lower_bound <= mental_status_score <= upper_bound:
            input_vector[4] = status
            break

    sleep_quality_mapping = {"Poor": -1, "Average": 0, "Good": 1}

    # Map binary inputs
    input_vector[5] = sleep_quality_mapping[sleep_quality]
    
        # Map extracurricular involvement ( -1, 0, 1 mapping as provided)
    cc_mapping = {'Low': -1, 'Moderate': 0, 'High': 1}
    input_vector[6] = cc_mapping[physical_activity]
    input_vector[7] = cc_mapping[extracurricular]
    
    # Map therapy ( -1, 0, 1 mapping as provided)
    service_mapping = {'Never': -1, 'Occasionally': 0, 'Frequently': 1}
    input_vector[8] = service_mapping[therapy]
    
    # Map one-hot encoded inputs for gender
    gender_mapping = {'Female': 0, 'Male': 1}
    input_vector[9] = gender_mapping[gender]  
    
    # Map one-hot encoded inputs for program
    course_mapping = {
        "Business": 11,
        "Computer Science": 12,
        "Engineering": 13,
        "Law": 14,
        "Medical": 15,
        "Others": 16
    }

    course_index = course_mapping.get(course, None)
    
    if course_index is not None:
        input_vector[course_index] = 1
    else:
        # Handle the case where the program is not in the course_mapping
        # For example, you could log a warning or set a default value
        st.warning("Selected program is not recognized. Please select a valid program.")

    return input_vector

# Function to display the assessment form and get predictions
def show_assessment_page():
    set_background("https://img.freepik.com/premium-photo/soft-pink-cloud-sky-colorful-pastel-tone_51012-646.jpg?size=626&ext=jpg")
    st.title("Mental Health Assessment")

    with st.form(key='assessment_form'):
        col1, col2, col3 = st.columns(3)

        with col1:
            age = st.number_input("What is your age?", 18,27,18) 
            course = st.selectbox(
                "What course or program are you currently enrolled in?",
                ("Medical", "Computer Science", "Law", "Business", "Engineering", "Others")
            )
            gender = st.radio("Please specify your gender.", ("Male", "Female"))
            cgpa = st.slider("What is your current Cumulative Grade Point Average (CGPA)?", 2.0, 4.0, 2.0)
            stress_level = st.slider("On a scale of 0-5, how would you rate your current level of stress?", 0, 5, 0)
        
        with col2:
            depression_level = st.slider("On a scale of 0-5, how would you rate your current level of depression?", 0, 5, 0)
            anxiety_level = st.slider("On a scale of 0-5, how would you rate your current level of anxiety?", 0, 5, 0)
            sleep_quality = st.selectbox("Would you describe your sleep quality as", ("Poor", "Average", "Good"))
            physical_activity = st.selectbox("How would you classify your level of physical activity:", ('Low', 'Moderate', 'High'))
        
        with col3:
            financial_stress = st.slider("On a scale of 0-5, how would you rate your level of financial stress?", 0, 5, 0)
            semester_credits = st.slider("How many credit hours are you enrolled in for the current semester?", 15, 30, 15)
            therapy = st.selectbox("Are you currently receiving any mental health support from a professional counselor or therapist?", ['Never', 'Occasionally', 'Frequently'])
            extracurricular = st.selectbox("How involved are you in extracurricular activities?", ['Low', 'Moderate', 'High'])


        submit_button = st.form_submit_button(label='Submit Assessment')

    if submit_button:

        # Map the inputs to numeric values
        input_data = map_inputs(age, course, gender, cgpa, stress_level, depression_level, anxiety_level, sleep_quality, physical_activity, financial_stress, semester_credits, therapy, extracurricular)
    
        # Predict (ensure your model expects the data in the same format)
        prediction_numeric = model.predict([input_data])[0]  # the model returns a list/array
    
       # Map the numeric prediction back to a human-readable label
        mental_status_mapping = {-1: "Poor", 0: "Average", 1: "Good"}
        prediction_label = mental_status_mapping.get(prediction_numeric, "Unknown")

        st.write("According to the answers you have provided, your mental health condition is ' ", prediction_label, "'.")

        st.write("Thank you for taking the assessment and welldone for taking the first step towards understanding your mental health!"
                 "Here's a summary of your responses:")
        summary = (
            f"Your mental wellbeing status is {prediction_label}.\n"
            f"                                                            \n"
            f"Age: {age}, Course: {course}, Gender: {gender}, CGPA: {cgpa}\n"
            f"Stress Level: {stress_level}, Depression Level: {depression_level}, Anxiety Level: {anxiety_level}\n"
            f"Sleep quality: {sleep_quality}, Physical activity: {physical_activity}, Financial stress: {financial_stress}\n"
            f"Credit Hours: {semester_credits}, Seeking counsultation: {depression_level}, Extracurricular Activities: {extracurricular}\n"
            f"                                                            \n"
            f"Please remember that these results are NOT a diagnosis.\n"
            f"If you find that you may be experiencing symptoms of depression, stress, or anxiety, it's essential to consider speaking with a therapist.\n"
        )
        st.write(summary)
    
        st.write("If you would like to download the result summary, click download")

        st.download_button("Download Results", summary, "assessment_summary.txt", "text/plain")
        

# Display the survey page
        
def save_responses(responses):
    # Define the file path
    file_path = 'C:/Users/myatk/OneDrive/Desktop/survey_responses.csv'
    new_df = pd.DataFrame([responses])

    if os.path.exists(file_path):
        existing_data = pd.read_csv(file_path)
        updated_data = pd.concat([existing_data, new_df], ignore_index=True)
    else:
        updated_data = new_df

    updated_data.to_csv(file_path, index=False)

